mapper.select:
SELECT ROWNUM INTNUM, BM_SEQ INTSEQ, BM_GET_USER STRWRITERCODE, UM.UM_NAME STRNAME, BM_TITLE STRTITLE, BM_GET_DATE DATGET_DATE, NVL(CNT.BV_CNT, 0) INTHIT, BM_DETAIL_COMMENT STRDETAILCOMMENT
FROM BOARD_MAIN BM LEFT OUTER JOIN USER_MAIN UM ON(BM.BM_GET_USER = UM.UM_CODE) LEFT OUTER JOIN BOARD_VIEW BV ON (BM.BM_SEQ = BV.BV_BM_SEQ) LEFT OUTER JOIN ( SELECT BV_BM_SEQ, COUNT(*) BV_CNT FROM BOARD_VIEW GROUP BY BV_BM_SEQ ) CNT ON (BM.BM_SEQ = CNT.BV_BM_SEQ)
WHERE BM_SEQ = #{intSeq }

mapper.update:
UPDATE BOARD_MAIN
SET BM_TITLE = #{strTitle}, BM_DETAIL_COMMENT = #{strDetailComment}, BM_EDIT_DATE = SYSDATE
WHERE BM_SEQ = #{intSeq}
